#include<iostream>#include<cmath>using namespace std; bool lucky(long long x){       if(x==0)return false;           while(x>0)      {            int r=x%10;            if(r!=4&&r!=7)            {                 return false;                                                                  }                       x/=10;      }       return true;        }         int main(){      long long n;      cin>>n;      int count=0;       long long i=n;      while(i>0)      {            int r=i%10;            if(r==4||r==7)            {                  count++;            }            i/=10;      }                   if(lucky(count))      {            cout<<"YES";      }      else      {            cout<<"NO";      }       return 0;       }