#include <iostream>#include <vector>using namespace std; void solve(int n) {       vector<int> ans;      int counter = 0;      int power = 1;      while (n > 0)      {            int r = n % 10;            if (r != 0)            {                  counter++;                  ans.push_back(r * power);            }            n = n / 10;            power *= 10;      }      cout << ans.size() << endl;      for (auto &i : ans)      {            cout << i << " ";      }} int main(){       int t;      cin >> t;       while (t-- > 0)      {            int n;            cin>>n;             solve(n);            cout << endl;      }      return 0;}