#include <iostream> #include <vector>using namespace std; vector<int> solve(vector<int> &nums, int n){       for (int i = 1; i <= n - 2; i++)      {            int left = -1;            int right = -1;            int e = nums[i];             for (int j = i + 1; j < n; j++)            {                   if (nums[j] < e)                  {                        right = j;                        break;                  }            }            for (int j = i - 1; j >= 0; j--)            {                   if (nums[j] < e)                  {                        left = j;                        break;                  }            }             if (left != -1 && right != -1)            {                  return {left + 1, i + 1, right + 1};            }      }       return {};} int main(){      ios::sync_with_stdio(false);      cin.tie(nullptr);       int t1;       cin >> t1;       while (t1-- > 0)      {             int n;            cin >> n;             vector<int> nums(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  nums[i] = temp;            }             vector<int> ans = solve(nums, n);            if (ans.size() != 0)            {                  cout << "YES" << endl;                  for (int i = 0; i < ans.size(); i++)                  {                        cout << ans[i] << " ";                  }                  cout << endl;                              }            else            {                  cout << "NO" << endl;            }      }       return 0;}