#include<bits/stdc++.h>using namespace std;   int main(){        int n,m;      cin>>n>>m;        vector<pair<int,int>>container(m,{0,0});         for(int i=0;i<m;i++)       {            int a,b;            cin>>a>>b;            container[i].first=a;            container[i].second=b;       }        sort(container.begin(),container.end(),[](auto &a,auto& b){                      return a.second>b.second;                  });       int ans=0;        for(auto &i:container)       {             if(n==0)break;            int take=min(n,i.first);            ans+=take*i.second;            n-=take;                   }        cout<<ans;         return 0;}