#include <iostream>#include <string>using namespace std; string solve(long long a, long long b, long long c){    if (2*b == a + c)        return "YES";     if (b != 0) {        long long num = a + c;        long long den = 2 * b;        if (num % den == 0 && num / den > 0)            return "YES";    }     if (a != 0) {        long long num = 2 * b - c;        if (num % a == 0 && num / a > 0)            return "YES";    }     if (c != 0) {        long long num = 2 * b - a;        if (num % c == 0 && num / c > 0)            return "YES";    }     return "NO";} int main(){       int t;      cin >> t;       while (t-- > 0)      {             long long a, b, c;            cin >> a >> b >> c;             cout << solve(a, b, c) << endl;      }       return 0;}