#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        int n;        cin >> n;        vector<long long> a(n);        for (int i = 0; i < n; i++) cin >> a[i];                sort(a.begin(), a.end());         int maxFreq = 1, cur = 1;        for (int i = 1; i < n; i++) {            if (a[i] == a[i - 1]) {                cur++;            } else {                maxFreq = max(maxFreq, cur);                cur = 1;            }        }        maxFreq = max(maxFreq, cur);                 if (maxFreq == n) {            cout << 0 << '\n';            continue;        }                int ops = 0;        int have = maxFreq;         while (have < n) {            ops++;             int add = min(have, n - have);            ops += add;             have += add;        }         cout << ops << '\n';    }    return 0;}