#include <bits/stdc++.h>using namespace std; bool isSubsequence(const string &s, const string &t){    int i = 0, j = 0;    while (i < s.size() && j < t.size())    {        if (s[i] == t[j]) j++;        i++;    }    return j == t.size();} string solve(string s, string t){    while (s.size() > t.size())    {        bool deleted = false;         for (char c = 'A'; c <= 'Z'; c++)        {            auto pos = s.find(c);            if (pos == string::npos) continue;             string ns = s;            ns.erase(pos, 1);             if (isSubsequence(ns, t))            {                s = ns;                deleted = true;                break;            }        }         if (!deleted)            return "NO";    }     return (s == t ? "YES" : "NO");} int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);     int tc;    cin >> tc;    while (tc--)    {        string s, t;        cin >> s >> t;        cout << solve(s, t) << '\n';    }}