#include <iostream>#include <numeric>#include <cmath>using namespace std; int digitCount(int number){      return 0;} int solve(int n){      int ans = 0;       if (n < 10)      {            return n;      }       ans += 9;      int expo = 1;      int counter = 1;      for (int i = 10; i <= n; i += pow(10, expo))      {             ans++;            if (counter == 10)            {                  expo++;                  counter = 1;            }            counter++;      }      return ans;} int main(){       int t;      cin >> t;       while (t-- > 0)      {            int n;            cin >> n;             cout << solve(n) << endl;      }       return 0;}