#include <iostream>#include <unordered_set>#include <vector>using namespace std; int main(){       int t;      cin >> t;       while (t-- > 0)      {             int n;            cin >> n;             vector<int> nums(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                   nums[i] = temp;            }            vector<int> demo(n, 0);            int start = 0;            int end = n - 1;            int index = 0;             while (start < end)            {                  demo[index++] = nums[start];                  demo[index++] = nums[end];                  start++;                  end--;            }            if (n % 2 != 0)            {                  demo[index] = nums[start];            }                         bool status = true;             int sum = 0;                        for (int i = 0; i < n; i++)            {                  if (demo[i]==sum)                  {                        status = false;                        break;                  }                  sum += demo[i];                             }             if (status)            {                  cout << "YES" << endl;                   for (int i = 0; i < n; i++)                  {                        cout << demo[i] << " ";                  }                  cout << endl;            }            else            {                  cout << "NO" << endl;            }      }       return 0;}