#include <vector>#include <iostream>#include <cmath> using namespace std;typedef long long ll; int solve(vector<int> &nums, int n){      int left =0;      int total=0;      for(int i=0;i<n;i++)      {            if(nums[i]==2)            {                  total++;            }      }       if(total==0)      {            return 1;      }       for(int i=0;i<n;i++)      {            if(nums[i]==0)continue;            if(nums[i]==2)            {                  left++;                  total--;            }            if(left==total)            {                  return i+1;            }      }             return -1;} int main(){       int t;      cin >> t;       while (t-- > 0)      {             int n;            cin >> n;             vector<int> nums(n, 0);             for (int i = 0; i < n; i++)            {                   int temp;                  cin >> temp;                   nums[i] = temp;            }            int ans = solve(nums, n);             cout << ans << endl;      }       return 0;}