#include<iostream>#include<string>using namespace std; int solve(string &str, int n){      int first = 0;      int last = n - 1;      int ans = n;      while (first < last && str[first] != str[last])      {            ans -= 2;            first++;            last--;      }       return ans;} int main(){       int t;      cin >> t;       while (t-- > 0)      {             int n;            cin >> n;             string str;            cin >> str;             int ans = solve(str, n);            cout << ans << endl;      }}