#include<iostream> #include<vector> using namespace std;  int solve(vector<int>&arr,int n)  {       int ans=0;       for(int i=0;i<arr.size();i++)      {            ans^=arr[i];                  }       if(n&1)      {            return ans;       }      else      {             if(ans==0)return 0;else return -1;       }          return -1;         }  int main() {       int t;      cin>>t;       while(t-- >0)      {            int n;            cin>>n;             vector<int>arr(n,0);             for(int i=0;i<n;i++)            {                  int temp;                  cin>>temp;                  arr[i]=temp;            }              cout<<solve(arr,n)<<endl;      }         return 0; }