#include <iostream>#include <string>using namespace std;typedef long long ll; string solve(ll n, ll k){      if (k % 2 == 1)            return "YES";      else       {            if (k % 2 == 0 && n % 2 == 0)                  return "YES";            else                  return "NO";      }} int main(){       int t;      cin >> t;       while (t-- > 0)      {             ll n, k;            cin >> n >> k;             cout << solve(n, k) << endl;      }       return 0;}