#include<iostream>#include<vector> using namespace std; int solve(vector<int>&nums,int n){       int ans=INT_MIN;      int counter=0;       for(int i=0;i<n;i++)      {            counter++;            if(nums[i]==1)            {                  counter=0;            }            ans=max(ans,counter);       }      return ans;}  int main(){        int t;      cin>>t;      while(t-- >0)      {              int n;            cin>>n;              vector<int>nums(n,0);             for(int i=0;i<n;i++)            {                  int temp;                  cin>>temp;                  nums[i]=temp;            }             cout<<solve(nums,n)<<endl;       }}