#include<iostream>using namespace std; void solve(int x,int k){       if(x%k!=0)      {            cout<<1<<endl;            cout<<x<<endl;      }      else      {            cout<<2<<endl<<x-1<<" "<<1<<endl;      }}   int main(){        int t;      cin>>t;      while(t-- >0 )      {            int x,k;            cin>>x>>k;               solve(x,k);      }          return 0;}