#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        int n;        cin >> n;        string s;        cin >> s;         vector<int> L(n), R(n);                 for (int i = 0; i < n; i++) {            if (s[i] == '<')                L[i] = (i > 0 ? L[i - 1] : 0) + 1;        }             for (int i = n - 1; i >= 0; i--) {            if (s[i] == '>')                R[i] = (i + 1 < n ? R[i + 1] : 0) + 1;        }         int ans = 1;        for (int i = 0; i < n; i++) {            ans = max(ans, L[i] + R[i] + 1);        }         cout << ans << "\n";    }    return 0;}