#include <iostream>#include <vector>#include <climits>using namespace std; int solve(vector<int> &nums, int n){       int gap = INT_MAX;       for (int i = 0; i + 1 < n; i++)      {             if (nums[i] > nums[i + 1])                  return 0;             gap = min(gap, nums[i + 1] - nums[i]);      }       return gap / 2 + 1;} int main(){       int t;      cin >> t;       while (t-- > 0)      {            int n;            cin >> n;             vector<int> nums(n, 0);             for (int i = 0; i < n; i++)            {                   int temp;                  cin >> temp;                  nums[i] = temp;            }             int ans = solve(nums, n);            cout << ans << endl;      }       return 0;}