#include <iostream>using namespace std; int main(){      int t;      cin >> t;       while (t-- > 0)      {             int n;            cin >> n;             int sum = 0;             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  sum += temp;            }             if (sum % 2 == 0)            {                  cout << "YES" << endl;            }            else            {                   cout << "NO" << endl;            }      }       return 0;}