#include <iostream>#include <vector>#include <algorithm>using namespace std; bool solve(vector<int> &nums, vector<int> &b, vector<int> &c){       sort(nums.begin(), nums.end());       int low = nums.front();       for (int &i : nums)      {            if (i == low)            {                  b.push_back(i);            }            else            {                  c.push_back(i);            }      }      for (int &i : c)      {            if (low%i == 0)                  return false;      }       return true;} void print(vector<int> &arr){      for (auto &i : arr)      {            cout << i << " ";      }} int main(){       int t;      cin >> t;      while (t-- > 0)      {             int n;            cin >> n;             vector<int> nums(n, 0);            for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  nums[i] = temp;            }            vector<int> b;            vector<int> c;             if (solve(nums, b, c))            {                  if (c.size() == 0)                  {                        cout << -1 << endl;                        continue;                  }                  else                  {                         cout << b.size() << " " << c.size() << endl;                        print(b);                        cout << endl;                        print(c);                        cout << endl;                  }            }            else            {                  cout<<-1<<endl;            }      }       return 0;}