#include <iostream>#include <vector>#include <cmath>#include <numeric>using namespace std; int main() {       int n;      cin >> n;       int ans = INT_MAX;      vector<int>nums(n,0);       for (int i = 0; i < n; i++)      {            int temp;            cin >> temp;           nums[i]=temp;                   }      for (int i = 0; i < n; i++)      {            if(nums[i]>0)           {            ans=min(ans,nums[i]);           }           else           {            ans=min(ans,0-nums[i]);           }      }       cout << ans << endl;       return 0;}