#include <iostream>#include <vector>using namespace std; int solve(vector<vector<char>> &grid){       int ans = 0;       for (int i = 0; i < 10; i++)      {            for (int j = 0; j < 10; j++)            {                   if (grid[i][j] == 'X')                  {                        int ring = min(min(i, j), min(9 - i, 9 - j));                         if (ring == 0)                              ans += 1;                        else if (ring == 1)                              ans += 2;                        else if (ring == 2)                              ans += 3;                        else if (ring == 3)                              ans += 4;                        else if (ring == 4)                              ans += 5;                  }                  else                  {                        continue;                  }            }      }       return ans;} int main(){       int t;      cin >> t;      while (t-- > 0)      {            int n = 10;             vector<vector<char>> grid(10, vector<char>(10, '\0'));             for (int i = 0; i < n; i++)            {                   for (int j = 0; j < n; j++)                  {                         char temp;                        cin >> temp;                        grid[i][j] = temp;                  }            }             cout << solve(grid) << endl;      }       return 0;}