#include <bits/stdc++.h>using namespace std; long long solve(vector<int> &a, vector<int> &b, int p, int n){    // min-heap: (bi, ai)    priority_queue<        pair<int,int>,        vector<pair<int,int>>,        greater<pair<int,int>>    > pq;     for(int i = 0; i < n; i++)        pq.push({b[i], a[i]});     long long ans = p;      // first direct    long long notified = 1;     while(notified < n && !pq.empty())    {        auto [bi, ai] = pq.top();        pq.pop();         if(bi >= p) break;  // no forwarding is useful now         // use this person's forwarding capacity        long long take = min((long long)ai, n - notified);        ans += take * bi;        notified += take;    }     // remaining via direct    ans += (n - notified) * 1LL * p;     return ans;} int main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while(t--)    {        int n, p;        cin >> n >> p;         vector<int> a(n), b(n);        for(int i = 0; i < n; i++) cin >> a[i];        for(int i = 0; i < n; i++) cin >> b[i];         cout << solve(a, b, p, n) << "\n";    }    return 0;}