#include <iostream>#include <vector>using namespace std; int solve(vector<int> &nums){       int sum = 0;      for (auto &i : nums)      {            sum += i;      }       return -sum;} int main(){       int t;      cin >> t;      while (t-- > 0)      {            int n;            cin >> n;             vector<int> nums(n - 1, 0);             for (int i = 0; i < n - 1; i++)            {                  int temp;                  cin >> temp;                  nums[i] = temp;            }             cout << solve(nums) << endl;      }       return 0;}