#include <string>#include <iostream> using namespace std; int solve(string x, string s){      int ans = 0;      while (x.size() < s.size())      {            ans++;            x = x + x;      }       if (x.find(s) != string::npos)            return ans;      x = x + x;      ans++;      if (x.find(s) != string::npos)            return ans;       return -1;} int main(){      int t;      cin >> t;      while (t--)      {            int n, m;            cin >> n >> m;            string x, s;            cin >> x >> s;            cout << solve(x, s) << "\n";      }}