#include<iostream>#include<string>#include<vector>#include<unordered_map>using namespace std; string solve(string& s,int k,int n){     vector<int>letter(26,0);     for(char &i:s)    {      letter[i-'a']++;    }    k++;     for(int i=0;i<26;i++)    {      int f=letter[i]%2;       if(f==0)continue;       else{            if(k-f>=0)            {                  k-=f;            }            else{                  return "NO";            }      }                     }     return "YES";  }     int main(){       int t;      cin>>t;       while(t-- > 0)      {                int n ,k;            cin>>n>>k;            string s;            cin>>s;              cout<<solve(s,k,n)<<endl;      }}