#include<iostream>#include<vector>#include<climits>using namespace std; int solve(vector<int>&nums,int n,int k){      if(k==2)      {            for(auto &i:nums)            {                  if(i%2==0)return 0;            }            return 1;      }       if(k==3)      {             int ans=INT_MAX;            for(int &x:nums)            {                  if(x%3==0)return 0;                  if(x%3==1)ans=min(ans,2);                 else if(x%3==2)ans=min(ans,1);            }            return ans;      }      if(k==5)      {            int ans=INT_MAX;             for(int &i:nums)            {                   if(i%5==0)return 0;                  else ans=min(ans,k-i%5);             }            return ans;      }              int cnt2 = 0;          int cnt4 = 0;         int best_make_4 = INT_MAX;     for (int x : nums) {        if (x % 4 == 0) cnt4++;        else if (x % 2 == 0) cnt2++;               best_make_4 = min(best_make_4, (4 - (x % 4)) % 4);    }      if (cnt4 > 0) return 0;    if (cnt2 >= 2) return 0;      int ans = best_make_4;    if (cnt2 == 1) ans = min(ans, 1);     else ans = min(ans, 2);               return ans;                      }   int main(){      int t;      cin>>t;      while(t-- >0)      {             int n,k;            cin>>n>>k;             vector<int>nums(n,0);              for(int i=0;i<n;i++)            {                  int temp;                  cin>>temp;                   nums[i]=temp;            }            cout<<solve(nums,n,k)<<endl;      }          return 0;}