#include <iostream>#include <vector>#include <algorithm>#include<unordered_map> using namespace std; bool check(vector<int> &arr, int n){           unordered_map<int,int>freq;       for(auto &i:arr)      {            freq[i]++;                 }        if(freq.size()>2)return false;      else if(freq.size()==2)      {             auto it=freq.begin();                     int count1=it->second;            it++;                      int count2=it->second;             if(abs(count1-count2)<=1)return true;            else return false;                                }       return true;                      } int main(){       int t;      cin >> t;       while (t-- > 0)      {             int n;            cin >> n;             vector<int> arr(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  arr[i] = temp;            }             if (check(arr, n))            {                  cout << "Yes" << endl;            }            else            {                  cout << "No" << endl;            }      }       return 0;}