#include <iostream> #include <string>#include <cmath>#include <algorithm>#include <vector> using namespace std; int main(){       int t;      cin >> t;      while (t--)      {             int n;            cin >> n;            string s;            cin >> s;            int dot = 0;            bool filler = false;             for (int i = 0; i < n;)            {                  if (s[i] == '#')                  {                        i++;                        continue;                  }                   int start = i;                   while (start < n && s[start] == '.')                  {                         start++;                  }                  int len = start - i;                  dot += len;                   if (len >= 3)                  {                         filler = true;                  }                  i = start;            }            if (filler)                  cout << 2 << endl;            else                  cout << dot << endl;      }      return 0;}