#include <iostream>#include <vector>#include <algorithm>#include <climits>#include <cmath> using namespace std; int main(){       int t;      cin >> t;      while (t-- > 0)      {             int n, x;            cin >> n >> x;            vector<int> arr(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  arr[i] = temp;            }             int ans=arr.front()-0;            for(int i=1;i<n;i++)            {                   ans=max(ans,arr[i]-arr[i-1]);              }             int last=x-arr.back();            last*=2;            ans=max(ans,last);            cout<<ans<<endl;      }       return 0;}