#include <iostream>#include <vector>#include <algorithm>#include <cmath>using namespace std; bool check(vector<int> &arr){      const int n = arr.size();      for (int i = 1; i < n; i++)      {            if (arr[i - 1] > arr[i])            {                  return false;            }      }       return true;} int main(){       int t;      cin >> t;      while (t-- > 0)      {             int n, k;            cin >> n >> k;             vector<int> arr(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  arr[i] = temp;            }            if (k >= 2)            {                  cout << "YES" << endl;            }            else            {                   if (check(arr))                  {                        cout << "YES" << endl;                  }                  else                  {                        cout << "NO" << endl;                  }            }      }       return 0;}