#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;     while (t--) {        long long a, b;        cin >> a >> b;         long long xK, yK, xQ, yQ;        cin >> xK >> yK;        cin >> xQ >> yQ;         // all possible knight move offsets        set<pair<long long, long long>> moves;        long long dx[4] = {a, a, -a, -a};        long long dy[4] = {b, -b, b, -b};         for (int i = 0; i < 4; i++) {            moves.insert({dx[i], dy[i]});            moves.insert({dy[i], dx[i]});        }         set<pair<long long, long long>> attackKing, attackQueen;         // positions from where knight attacks King        for (auto [dx, dy] : moves) {            attackKing.insert({xK + dx, yK + dy});        }         // count positions that also attack Queen        int ans = 0;        for (auto [dx, dy] : moves) {            pair<long long, long long> pos = {xQ + dx, yQ + dy};            if (attackKing.count(pos)) {                ans++;            }        }         cout << ans << "\n";    }    return 0;}