#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        string s;        cin >> s;        int n = s.size();         int cnt0 = 0, cnt1 = 0;        for (char c : s) {            if (c == '0') cnt0++;            else cnt1++;        }         int z = 0, o = 0;        int L = 0;         for (int i = 0; i < n; i++) {            if (s[i] == '0') z++;            else o++;             if (z <= cnt1 && o <= cnt0) {                L = i + 1;            } else {                break;            }        }         cout << n - L << '\n';    }    return 0;}