#include<iostream>using namespace std; int main(){        int testcase;      cin>>testcase;      while(testcase!=0)      {             testcase--;             int num1;            int num2;            cin>>num1>>num2;              if(num1==num2)            {                  cout<<"0"<<endl;            }            else if(num1%num2==0||num2%num1==0)            {                  cout<<"1"<<endl;              }             else             {                  cout<<"2"<<endl;             }      }}