#include<iostream>#include<vector>using namespace std; int main(){       int testcase;      cin>>testcase;      while(testcase!=0)      {            testcase--;            int size;            cin>>size;            vector<int>arr(size);             for(int i=0;i<size;i++)            {                                    cin>>arr[i];            }            int zero=0,neg=0,pos=0;             for(auto &i:arr)            {                  if(i==0)                  {                        zero++;                  }                  else if(i==-1)                  {                        neg++;                  }                  else                  {                        pos++;                  }                         }            int ans=0;             if(zero>0)            {                  ans+=zero;            }           if(neg%2!=0)            {                  ans+=2*(neg%2);            }             cout<<ans<<endl;      }}