import java.util.*;fun main() {    val t = readLine()!!.toInt()  // number of testcases     repeat(t) {        val size = readLine()!!.toInt()        val arr = readLine()!!.trim().split(" ").map { it.toInt() }         var zero = 0        var neg = 0         for (num in arr) {            when (num) {                0 -> zero++                -1 -> neg++            }        }         var ans = 0        if (zero > 0) ans += zero        if (neg % 2 != 0) ans += 2 * (neg % 2)         println(ans)    }}
