#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);        int t;    if (!(cin >> t)) return 0;    while (t--) {        int n; cin >> n;        vector<int> cnt(n+1, 0);         for (int i = 0; i < n; ++i) {            int x; cin >> x;            if (0 <= x && x <= n) cnt[x]++;        }         int ans = 0;                for (int x = 0; x <= n; ++x) {            int f = cnt[x];            if (f < x) ans += f;                  else ans += (f - x);             }        cout << ans << '\n';    }    return 0;}