#include <iostream>using namespace std; int main() {    int t;     cin >> t;    while (t--) {        long long R0, X;        int D, n;        cin >> R0 >> X >> D >> n;        string s;        cin >> s;         long long low = R0, high = R0;        int ans = 0;         for (char c : s) {            if (c == '1') { // div.1 → always rated                ans++;                low = max(0LL, low - D);                high = high + D;            } else { // div.2                if (low >= X) {                    // all ratings >= X → unrated                    continue;                }                ans++;                low = max(0LL, low - D);                high = min(high + D, X - 1);            }        }        cout << ans << "\n";    }}