#include <bits/stdc++.h>using namespace std;int solve(string &str){     const int n=str.size();    char ch=str[n-1];    int ans=0;    for(int i=n-2;i>=0;i--)    {      if(ch!=str[i])ans++;    }    return ans;} int main(){      int t;      cin >> t;      while (t-- > 0)       {            int n;            cin>>n;            string str;            cin >> str;            cout << solve(str) << endl;      }       return 0;}