#include <iostream>#include <vector>#include <algorithm>#include <cmath>#include <string>using namespace std; int main(){       int t;      cin >> t;      while (t-- > 0)      {             int n;            cin >> n;            vector<string> arr(n, "");             for (int i = 0; i < n; i++)            {                  string temp;                  cin >> temp;                  arr[i] = temp;            }                 string ans = arr[0];       for (int i = 1; i < n; i++)      {            string temp = arr[i];             if(ans+temp>temp+ans)            {                  ans=temp+ans;            }            else            {                  ans=ans+temp;            }                            }      cout << ans << endl;} return 0;}