#include <iostream>using namespace std; int solve(int a, int b,bool start) {      bool white=start;                 long long sum = 1;      int counter = 0;      while (b > 0|| a > 0)      {            if (white)            {                  if( a <sum)break;                  a-=sum;                              }            else            {                  if( b <sum)break;                                                      b -= sum;            }            counter++;            white = !white;            sum <<=1;      }       return counter;} int main(){       int t;      cin >> t;      while (t-- > 0)      {             int a, b;            cin >> a >> b;            int ans=max(solve(a,b,true),solve(a,b,false));             cout << ans<<endl;      }       return 0;}