#include <bits/stdc++.h>using namespace std;int solve(vector<int>&nums,int n){          int idx=-1;     int high=0;       for(int i=0;i<n;i++)      {            if(nums[i]>high)            {                  high=nums[i];                  idx=i;            }      }      swap(nums[0],nums[idx]);          int ans=0;      int h=0;             for(auto &i:nums)      {        h=max(h,i);        ans+=h;      }      return ans;}    int main(){       int t;      cin>>t;      while(t-- >0)      {            int n;            cin>>n;            vector<int>nums(n,0);              for(auto i=0;i<n;i++)            {                  int temp;                  cin>>temp;                  nums[i]=temp;            }             cout<<solve(nums,n)<<endl;       }          return 0;}