#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        int n, h, l;        cin >> n >> h >> l;         int R = 0, C = 0, B = 0;         for (int i = 0; i < n; i++) {            int x;            cin >> x;             if (x <= h && x <= l) {                B++;                  } else if (x <= h) {                R++;                 } else if (x <= l) {                C++;                  }        }         int ans = min({R + B, C + B, (R + C + B) / 2});        cout << ans << "\n";    }     return 0;}