#include <iostream>#include <vector>#include <string>#include <algorithm>using namespace std; string solve(vector<int> &nums, int n){       vector<pair<int, int>> vec;       for (int i = 0; i < n; i++)      {            vec.push_back({nums[i], i});      }       sort(vec.begin(), vec.end(), [](auto &a, auto &b)           { return a.first < b.first; });       for (int i = 1; i < n; i++)      {            if ((vec[i].second % 2) == (vec[i - 1].second % 2))            {                  return "NO";            }            else            {                  continue;            }      }       return "YES";} int main(){       int t;      cin >> t;      while (t-- > 0)      {             int n;            cin >> n;             vector<int> nums(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                   nums[i] = temp;            }             cout << solve(nums, n) << endl;      }       return 0;}