#include<iostream>#include<vector>#include<algorithm>#include<numeric>#include<string>using namespace std;  string solve(vector<int>&nums,int s,int x){        int sum=accumulate(nums.begin(),nums.end(),0);        if(sum==s)return "YES";      if(sum>s)return "NO";       if((s-sum)%x==0)return "YES";       return "NO";   }   int main(){       int t;      cin>>t;      while(t-- >0)      {            int n,s,x;            cin>>n>>s>>x;              vector<int>nums(n,0);             for(int i=0;i<n;i++)            {                  int temp;                  cin>>temp;                  nums[i]=temp;            }             cout<<solve(nums,s,x)<<endl;      }       return 0;}