#include<iostream>#include<vector>using namespace std; vector<int> solve(vector<int>&a,vector<int>&b,vector<pair<int,int>>&query){      vector<int>ans;       const int n=a.size();      for(int i=0;i+1<a.size();i++)      {            a[i]=max(a[i+1],max(b[i],a[i]));       }      a[n-1]=max(a[n-1],b[n-1]);       for(int i=n-2;i>=0;i--)      {            a[i]=max(a[i+1],max(a[i],b[i]));      }       vector<int>prefix(n,0);      prefix[0]=a[0];       for(int i=1;i<n;i++)      {                                         prefix[i]=a[i]+prefix[i-1];                  }        for(auto &i:query)      {            int start=i.first;            int end=i.second;          if(start==1)          {            ans.push_back(prefix[end-1]);          }          else{            ans.push_back(prefix[end-1]-prefix[start-2]);          }      }        return ans;}  int main(){       int t;      cin>>t;       while(t-- >0)      {            int n;            int q;            cin>>n>>q;             vector<int>a(n,0);            for(int i=0;i<n;i++)            {                  int temp;                  cin>>temp;                  a[i]=temp;            }            vector<int>b(n,0);             for(int i=0;i<n;i++)            {                  int temp;                  cin>>temp;                  b[i]=temp;            }            vector<pair<int,int>>query(q,{0,0});             for(int i=0;i<q;i++)            {                  int l,r;                  cin>>l>>r;                  query[i].first=l;                  query[i].second=r;            }            vector<int>ans=solve(a,b,query);            for(auto &i:ans)           {            cout<<i<<" ";           }           cout<<endl;      }        return 0;}