#include <iostream>#include <vector>#include <algorithm>using namespace std; long long maxLevel(vector<long long> &pb, long long S){      int start = 0;      int end = pb.size() - 1;      int ans = -1;       while (start <= end)      {            int mid = start + (end - start) / 2;            if (pb[mid] <= S)            {                  ans = mid + 1;                  start = mid + 1;            }            else            {                  end = mid - 1;            }      }      return ans;} int sword(    vector<int> &a, int x ){      int start = 0, end = a.size() - 1;      int idx = a.size();       while (start <= end)      {            int mid = start + (end - start) / 2;             if (a[mid] >= x)            {                  idx = mid;                  end = mid - 1;            }            else            {                  start = mid + 1;            }      }      return a.size() - idx;} long long solve(vector<int> &a, vector<int> &b){           const int n = a.size();      vector<long long> pb(n, 0);      pb[0] = b[0];       for (int i = 1; i < n; i++)      {            pb[i] = b[i] + pb[i - 1];      }       sort(a.begin(), a.end());       long long ans = 0;       for (int i = 0; i < n; i++)      {             if (i > 0 && a[i] == a[i - 1])                  continue;             int x = a[i];             int s = sword(a, x);            int k = maxLevel(pb, s);            ans = max(ans, 1LL * x * k);      }        return ans;} int main(){       int t;      cin >> t;       while (t-- > 0)      {             int n;            cin >> n;             vector<int> a(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                   a[i] = temp;            }            vector<int> b(n, 0);             for (int i = 0; i < n; i++)            {                  int temp;                  cin >> temp;                  b[i] = temp;            }             cout << solve(a, b) << endl;      }       return 0;}