#include<iostream>#include<vector>#include<algorithm>#include<string>using namespace std;string solve(vector<int>&nums,int n){      sort(nums.begin(),nums.end());        int low=nums.front();       for(int i=0;i<n;i++)      {            if(low<nums[i])            {                  return to_string(nums[i]);            }      }      return "NO";}  int main(){      int n;      cin>>n;       vector<int>nums(n,0);      for(int i=0;i<n;i++)      {            int temp;cin>>temp;            nums[i]=temp;      }       cout<<solve(nums,n);       return 0;}