#include <iostream>using namespace std; int main(){       string s;      cin >> s;       int l = 0;      int u = 0;      for (auto &i : s)      {            if (i >= 65 && i <= 90)            {                  u++;            }            else            {                  l++;            }      }       if (l > u || l == u)      {             for (int i = 0; i < s.size(); i++)            {                  if (s[i] >= 65 && s[i] <= 90)                  {                        s[i] = s[i] + 32;                  }            }            cout << s << endl;      }      else      {            for (int i = 0; i < s.size(); i++)            {                  if (s[i] >= 97 && s[i] <= 122)                  {                        s[i] = s[i] - 32;                  }            }            cout << s << endl;      }}