#include <iostream>#include <vector>using namespace std; int main() {       int person;      cin >> person;      int h;      cin >> h;      vector<int> arr(person);       for (int i = 0; i < person; i++)      {            cin >> arr[i];      }       int w = 0;      for (auto &i : arr)      {            if (i > h)            {                  w += 2;            }            else            {                  w += 1;            }      }       cout << w << endl;}