#include<iostream>#include<algorithm>#include<numeric>using namespace std;  int main(){       int y,w;      cin>>y>>w;             if(y==w)      {         int total=6-y+1;         int divider=gcd(total,6);         int num=total/divider;         int den=6/divider;          cout<<num<<"/"<<den<<endl;        }      else      {            int total=6-max(y,w)+1;            int divider=gcd(total,6);            int num=total/divider;            int den=6/divider;              cout<<num<<"/"<<den<<endl;      }         return 0;}